// transaction-list.component.ts - ÂÆåÊï¥ÁâàÔºàÂåπÈÖçHTMLÔºâ
import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { TransactionService, Transaction } from '../../../shared/services/transaction.service';

@Component({
  selector: 'app-transaction-list',
  templateUrl: './transaction-list.component.html',
  styleUrls: ['./transaction-list.component.scss']
})
export class TransactionListComponent implements OnInit {
  transactions: Transaction[] = [];
  filteredTransactions: Transaction[] = [];
  isLoading = false;
  errorMessage = '';

  // Ê±áÊÄªÊï∞ÊçÆ
  monthlyIncome: number = 0;
  monthlyExpense: number = 0;
  netSavings: number = 0;

  // Á≠õÈÄâ
  selectedType: 'ALL' | 'INCOME' | 'EXPENSE' = 'ALL';
  searchTerm: string = '';

  // ÂΩìÂâçËßÜÂõæ
  currentView: 'list' | 'calendar' = 'list';

  constructor(
    private transactionService: TransactionService,
    private router: Router,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    // ÁõëÂê¨Ë∑ØÁî±ÂèÇÊï∞ÔºàÁî®‰∫éÂà∑Êñ∞Ôºâ
    this.route.queryParams.subscribe(params => {
      if (params['refresh'] === 'true') {
        console.log('Transaction List: Ê£ÄÊµãÂà∞Âà∑Êñ∞ÂèÇÊï∞ÔºåÈáçÊñ∞Âä†ËΩΩÊï∞ÊçÆ...');
        this.loadTransactions();
      } else {
        this.loadTransactions();
      }
    });
  }

  /**
   * Âä†ËΩΩ‰∫§ÊòìÂàóË°®
   */
  loadTransactions(): void {
    this.isLoading = true;
    this.errorMessage = '';

    console.log('Transaction List: ÂºÄÂßãÂä†ËΩΩ‰∫§ÊòìÂàóË°®...');

    this.transactionService.getTransactions(0, 100).subscribe({
      next: (response) => {
        console.log('Transaction List: ‰∫§ÊòìÂä†ËΩΩÊàêÂäü:', response);

        // Â§ÑÁêÜÂàÜÈ°µÂìçÂ∫î
        if (response && 'content' in response) {
          this.transactions = response.content;
        } else {
          this.transactions = response as any;
        }

        console.log('Transaction List: ÂΩìÂâç‰∫§ÊòìÊï∞Èáè:', this.transactions.length);

        // ËÆ°ÁÆóÊ±áÊÄª
        this.calculateSummary();

        // Â∫îÁî®Á≠õÈÄâ
        this.applyFilters();

        this.isLoading = false;
      },
      error: (error) => {
        console.error('Transaction List: Âä†ËΩΩ‰∫§ÊòìÂ§±Ë¥•:', error);
        this.errorMessage = 'Êó†Ê≥ïÂä†ËΩΩ‰∫§ÊòìÂàóË°®ÔºåËØ∑Ê£ÄÊü•ÁΩëÁªúËøûÊé•';
        this.isLoading = false;
      }
    });
  }

  /**
   * ËÆ°ÁÆóÊ±áÊÄªÊï∞ÊçÆ
   */
  calculateSummary(): void {
    this.monthlyIncome = 0;
    this.monthlyExpense = 0;

    const currentMonth = new Date().getMonth();
    const currentYear = new Date().getFullYear();

    this.transactions.forEach(t => {
      const transDate = new Date(t.transactionDate);

      // Âè™ËÆ°ÁÆóÂΩìÊúàÁöÑ
      if (transDate.getMonth() === currentMonth && 
          transDate.getFullYear() === currentYear) {
        if (t.transactionType === 'INCOME') {
          this.monthlyIncome += t.amount;
        } else {
          this.monthlyExpense += t.amount;
        }
      }
    });

    this.netSavings = this.monthlyIncome - this.monthlyExpense;

    console.log('Transaction List: Ê±áÊÄªËÆ°ÁÆóÂÆåÊàê', {
      income: this.monthlyIncome,
      expense: this.monthlyExpense,
      net: this.netSavings
    });
  }

  /**
   * Â∫îÁî®Á≠õÈÄâ
   */
  applyFilters(): void {
    let filtered = [...this.transactions];

    // ÊåâÁ±ªÂûãÁ≠õÈÄâ
    if (this.selectedType !== 'ALL') {
      filtered = filtered.filter(t => t.transactionType === this.selectedType);
    }

    // ÊåâÊêúÁ¥¢ËØçÁ≠õÈÄâ
    if (this.searchTerm && this.searchTerm.trim()) {
      const term = this.searchTerm.toLowerCase();
      filtered = filtered.filter(t => 
        (t.description && t.description.toLowerCase().includes(term)) ||
        (t.categoryName && t.categoryName.toLowerCase().includes(term)) ||
        (t.notes && t.notes.toLowerCase().includes(term))
      );
    }

    this.filteredTransactions = filtered;
    console.log('Transaction List: Á≠õÈÄâÂêé‰∫§ÊòìÊï∞Èáè:', this.filteredTransactions.length);
  }

  /**
   * ÊåâÁ±ªÂûãÁ≠õÈÄâ
   */
  filterByType(type: 'ALL' | 'INCOME' | 'EXPENSE'): void {
    this.selectedType = type;
    this.applyFilters();
  }

  /**
   * ÊêúÁ¥¢
   */
  onSearch(): void {
    this.applyFilters();
  }

  /**
   * ÊâãÂä®Âà∑Êñ∞
   */
  refresh(): void {
    console.log('Transaction List: ÊâãÂä®Âà∑Êñ∞...');
    this.loadTransactions();
  }

  /**
   * ÂàáÊç¢ËßÜÂõæ
   */
  switchView(view: 'list' | 'calendar'): void {
    this.currentView = view;
  }

  /**
   * Ê∑ªÂä†‰∫§Êòì
   */
  addTransaction(): void {
    this.router.navigate(['/transactions/add']);
  }

  /**
   * Êü•Áúã‰∫§ÊòìËØ¶ÊÉÖ
   */
  viewTransaction(transaction: Transaction): void {
    this.router.navigate(['/transactions', transaction.id]);
  }

  /**
   * ÁºñËæë‰∫§Êòì
   */
  editTransaction(transaction: Transaction, event?: Event): void {
    if (event) {
      event.stopPropagation();
    }
    this.router.navigate(['/transactions', transaction.id, 'edit']);
  }

  /**
   * Âà†Èô§‰∫§Êòì
   */
  deleteTransaction(transaction: Transaction, event?: Event): void {
    if (event) {
      event.stopPropagation();
    }

    if (!confirm(`Á°ÆÂÆöË¶ÅÂà†Èô§ËøôÊù°‰∫§ÊòìÂêóÔºü\n${transaction.description || 'Êó†ÊèèËø∞'}`)) {
      return;
    }

    this.transactionService.deleteTransaction(transaction.id!).subscribe({
      next: () => {
        console.log('Transaction List: Âà†Èô§ÊàêÂäüÔºåÈáçÊñ∞Âä†ËΩΩÂàóË°®...');
        this.loadTransactions();
      },
      error: (error) => {
        console.error('Transaction List: Âà†Èô§Â§±Ë¥•:', error);
        alert('Âà†Èô§Â§±Ë¥•ÔºåËØ∑ÈáçËØï');
      }
    });
  }

// /**
//     * Âà†Èô§‰∫§ÊòìÔºàÂÖºÂÆπ ID ÊàñÂØπË±°Ôºâ
//   */
//   deleteTransaction(transactionOrId: Transaction | number, event?: Event): void {
//     // ÂÖºÂÆπ‰∏§ÁßçË∞ÉÁî®ÊñπÂºè
