// analytics.component.ts - Ê∑ªÂä† Net Worth Ê†áÁ≠æ

import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-analytics',
  templateUrl: './analytics.component.html',
  styleUrls: ['./analytics.component.scss']
})
export class AnalyticsComponent implements OnInit {

  selectedTab: 'charts' | 'networth' = 'charts';

  // Chart data
  incomeExpenseData: any;
  categoryBreakdownData: any;

  // Net Worth data
  netWorth = 0;
  totalAssets = 0;
  totalLiabilities = 0;
  assets: any[] = [];
  liabilities: any[] = [];

  constructor() {}

  ngOnInit(): void {
    this.loadChartData();
    this.loadNetWorthData();
  }

  selectTab(tab: 'charts' | 'networth'): void {
    this.selectedTab = tab;
  }

  loadChartData(): void {
    // Mock chart data
    this.incomeExpenseData = {
      labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
      datasets: [
        {
          label: 'Income',
          data: [5000, 5200, 5000, 5500, 5300, 5000],
          backgroundColor: 'rgba(75, 192, 192, 0.2)',
          borderColor: 'rgba(75, 192, 192, 1)',
          borderWidth: 2
        },
        {
          label: 'Expense',
          data: [3000, 3200, 2800, 3500, 3100, 2900],
          backgroundColor: 'rgba(255, 99, 132, 0.2)',
          borderColor: 'rgba(255, 99, 132, 1)',
          borderWidth: 2
        }
      ]
    };

    this.categoryBreakdownData = {
      labels: ['Housing', 'Food', 'Transportation', 'Entertainment', 'Other'],
      datasets: [{
        data: [1200, 600, 400, 300, 500],
        backgroundColor: [
          '#FF6384',
          '#36A2EB',
          '#FFCE56',
          '#4BC0C0',
          '#9966FF'
        ]
      }]
    };
  }

  loadNetWorthData(): void {
    // Mock Net Worth data
    this.assets = [
      { type: 'CASH', name: 'Savings Account', value: 15000 },
      { type: 'INVESTMENT', name: 'Stock Portfolio', value: 28750, autoCalculated: true },
      { type: 'REAL_ESTATE', name: 'Primary Home', value: 300000 }
    ];

    this.liabilities = [
      {
        id: 1,
        type: 'MORTGAGE',
        name: 'Home Mortgage',
        remainingBalance: 250000,
        monthlyPayment: 1347.13,
        progressPercent: 16.67
      },
      {
        id: 2,
        type: 'CAR_LOAN',
        name: 'Car Loan',
        remainingBalance: 15000,
        monthlyPayment: 450,
        progressPercent: 60
      }
    ];

    this.totalAssets = this.assets.reduce((sum, a) => sum + a.value, 0);
    this.totalLiabilities = this.liabilities.reduce((sum, l) => sum + l.remainingBalance, 0);
    this.netWorth = this.totalAssets - this.totalLiabilities;
  }

  openAddAssetModal(): void {
    alert('Add Asset modal - TODO');
  }

  openAddLoanModal(): void {
    alert('Add Loan modal - TODO');
  }

  editAsset(asset: any): void {
    alert('Edit Asset - TODO');
  }

  deleteAsset(asset: any): void {
    if (confirm(`Delete ${asset.name}?`)) {
      alert('Delete Asset - TODO');
    }
  }

  viewLoanDetails(loan: any): void {
    alert('View Loan Details - TODO');
  }

  deleteLoan(loan: any): void {
    if (confirm(`Delete ${loan.name}?`)) {
      alert('Delete Loan - TODO');
    }
  }
}
