<div class="analytics-container">

  <!-- Tab Navigation -->
  <div class="tab-navigation">
    <button 
      class="tab-btn" 
      [class.active]="selectedTab === 'charts'"
      (click)="selectTab('charts')">
      üìä Charts
    </button>
    <button 
      class="tab-btn" 
      [class.active]="selectedTab === 'networth'"
      (click)="selectTab('networth')">
      üíé Net Worth
    </button>
  </div>

  <!-- Charts Tab -->
  <div class="tab-content" *ngIf="selectedTab === 'charts'">
    <h2>Financial Analytics</h2>

    <div class="chart-section">
      <h3>Income vs Expense (Last 6 Months)</h3>
      <div class="chart-placeholder">
        <p>üìä Chart will be displayed here</p>
        <p>Income vs Expense Line Chart</p>
      </div>
    </div>

    <div class="chart-section">
      <h3>Expense by Category</h3>
      <div class="chart-placeholder">
        <p>üç∞ Pie Chart will be displayed here</p>
        <p>Category Breakdown</p>
      </div>
    </div>
  </div>

  <!-- Net Worth Tab -->
  <div class="tab-content" *ngIf="selectedTab === 'networth'">

    <!-- Net Worth Summary -->
    <div class="networth-summary">
      <div class="summary-card main">
        <div class="label">Net Worth</div>
        <div class="value" [class.positive]="netWorth >= 0" [class.negative]="netWorth < 0">
          ${{ netWorth.toLocaleString('en-US', {minimumFractionDigits: 2}) }}
        </div>
      </div>

      <div class="summary-row">
        <div class="summary-card">
          <div class="label">Total Assets</div>
          <div class="value green">
            ${{ totalAssets.toLocaleString('en-US', {minimumFractionDigits: 2}) }}
          </div>
        </div>

        <div class="summary-card">
          <div class="label">Total Liabilities</div>
          <div class="value red">
            ${{ totalLiabilities.toLocaleString('en-US', {minimumFractionDigits: 2}) }}
          </div>
        </div>
      </div>
    </div>

    <!-- Assets Section -->
    <div class="section">
      <div class="section-header">
        <h3>üí∞ Assets</h3>
        <button class="btn-add" (click)="openAddAssetModal()">+ Add Asset</button>
      </div>

      <div class="asset-list">
        <div class="asset-item" *ngFor="let asset of assets">
          <div class="asset-icon">
            <span *ngIf="asset.type === 'CASH'">üí∞</span>
            <span *ngIf="asset.type === 'INVESTMENT'">üìà</span>
            <span *ngIf="asset.type === 'REAL_ESTATE'">üè†</span>
            <span *ngIf="asset.type === 'VEHICLE'">üöó</span>
            <span *ngIf="asset.type === 'OTHER'">üì¶</span>
          </div>

          <div class="asset-info">
            <div class="asset-name">{{ asset.name }}</div>
            <div class="asset-type">{{ asset.type }}</div>
            <div class="auto-sync" *ngIf="asset.autoCalculated">üîÑ Auto-synced</div>
          </div>

          <div class="asset-value">
            ${{ asset.value.toLocaleString('en-US', {minimumFractionDigits: 2}) }}
          </div>

          <div class="asset-actions">
            <button class="btn-icon" (click)="editAsset(asset)" *ngIf="!asset.autoCalculated">‚úèÔ∏è</button>
            <button class="btn-icon" (click)="deleteAsset(asset)" *ngIf="!asset.autoCalculated">üóëÔ∏è</button>
          </div>
        </div>
      </div>
    </div>

    <!-- Liabilities Section -->
    <div class="section">
      <div class="section-header">
        <h3>üìä Liabilities</h3>
        <button class="btn-add" (click)="openAddLoanModal()">+ Add Loan</button>
      </div>

      <div class="liability-list">
        <div class="liability-item" *ngFor="let loan of liabilities" (click)="viewLoanDetails(loan)">
          <div class="loan-icon">
            <span *ngIf="loan.type === 'MORTGAGE'">üè†</span>
            <span *ngIf="loan.type === 'CAR_LOAN'">üöó</span>
            <span *ngIf="loan.type === 'STUDENT_LOAN'">üéì</span>
            <span *ngIf="loan.type === 'PERSONAL_LOAN'">üí≥</span>
            <span *ngIf="loan.type === 'OTHER'">üìÑ</span>
          </div>

          <div class="loan-info">
            <div class="loan-name">{{ loan.name }}</div>
            <div class="loan-details">
              <span class="monthly-payment">${{ loan.monthlyPayment.toFixed(2) }}/month</span>
            </div>

            <!-- Progress Bar -->
            <div class="progress-bar">
              <div class="progress-fill" [style.width.%]="loan.progressPercent"></div>
            </div>
            <div class="progress-text">{{ loan.progressPercent.toFixed(0) }}% paid</div>
          </div>

          <div class="loan-balance">
            <div class="balance-label">Remaining</div>
            <div class="balance-value">
              ${{ loan.remainingBalance.toLocaleString('en-US', {minimumFractionDigits: 2}) }}
            </div>
          </div>

          <button class="btn-icon delete" (click)="deleteLoan(loan); $event.stopPropagation()">üóëÔ∏è</button>
        </div>
      </div>

      <div class="empty-state" *ngIf="liabilities.length === 0">
        <p>No loans yet</p>
        <button (click)="openAddLoanModal()">+ Add Your First Loan</button>
      </div>
    </div>

  </div>

</div>
