// transaction-add.component.ts - ÂÆåÊï¥ÂèØË¶ÜÁõñÁâàÊú¨

import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { TransactionService } from '../../../core/services/transaction.service';

@Component({
  selector: 'app-transaction-add',
  templateUrl: './transaction-add.component.html',
  styleUrls: ['./transaction-add.component.scss']
})
export class TransactionAddComponent implements OnInit {

  transactionForm!: FormGroup;
  transactionType: 'INCOME' | 'EXPENSE' = 'EXPENSE';
  incomeCategories: any[] = [];
  expenseCategories: any[] = [];

  constructor(
    private fb: FormBuilder,
    private router: Router,
    private transactionService: TransactionService
  ) {}

  ngOnInit(): void {
    this.initForm();
    this.loadCategories();
  }

  initForm(): void {
    this.transactionForm = this.fb.group({
      transactionType: ['EXPENSE', Validators.required],
      categoryId: ['', Validators.required],
      amount: ['', [Validators.required, Validators.min(0.01)]],
      transactionDate: [new Date().toISOString().split('T')[0], Validators.required],
      description: [''],  // Optional now
      notes: ['']
    });
  }

  loadCategories(): void {
    // Mock categories - ÂÆûÈôÖÂ∫îËØ•‰ªé service Âä†ËΩΩ
    this.incomeCategories = [
      { id: 1, name: 'Salary', icon: 'üí∞' },
      { id: 2, name: 'Side Hustle', icon: 'üíº' },
      { id: 3, name: 'Rebate', icon: 'üéÅ' },
      { id: 4, name: 'Interest Income', icon: 'üìà' },
      { id: 5, name: 'Refund', icon: '‚Ü©Ô∏è' },
      { id: 6, name: 'Government Benefits', icon: 'üèõÔ∏è' },
      { id: 7, name: 'Other Income', icon: 'üíµ' }
    ];

    this.expenseCategories = [
      { id: 8, name: 'Housing', icon: 'üè†' },
      { id: 9, name: 'Utilities', icon: 'üí°' },
      { id: 10, name: 'Groceries', icon: 'üõí' },
      { id: 11, name: 'Dining Out', icon: 'üçî' },
      { id: 12, name: 'Transportation', icon: 'üöó' },
      { id: 13, name: 'Healthcare', icon: 'üè•' },
      { id: 14, name: 'Entertainment', icon: 'üé¨' },
      { id: 15, name: 'Shopping', icon: 'üõçÔ∏è' },
      { id: 16, name: 'Insurance', icon: 'üõ°Ô∏è' },
      { id: 17, name: 'Education', icon: 'üìö' },
      { id: 18, name: 'Other Expense', icon: 'üí∏' }
    ];
  }

  selectTransactionType(type: 'INCOME' | 'EXPENSE'): void {
    this.transactionType = type;
    this.transactionForm.patchValue({ transactionType: type });
    // Reset category when switching type
    this.transactionForm.patchValue({ categoryId: '' });
  }

  selectCategory(categoryId: number): void {
    this.transactionForm.patchValue({ categoryId });
  }

  get currentCategories(): any[] {
    return this.transactionType === 'INCOME' ? this.incomeCategories : this.expenseCategories;
  }

  onSubmit(): void {
    if (this.transactionForm.valid) {
      const formData = this.transactionForm.value;

      this.transactionService.createTransaction(formData).subscribe({
        next: (response: any) => {
          console.log('Transaction saved:', response);
          // Navigate back to transactions list
          this.router.navigate(['/transactions']);
        },
        error: (error: any) => {
          console.error('Error saving transaction:', error);
          alert('Failed to save transaction. Please try again.');
        }
      });
    } else {
      // Mark all fields as touched to show validation errors
      Object.keys(this.transactionForm.controls).forEach(key => {
        this.transactionForm.get(key)?.markAsTouched();
      });
      alert('Please fill in all required fields');
    }
  }

  cancel(): void {
    this.router.navigate(['/transactions']);
  }

  goBack(): void {
    this.router.navigate(['/transactions']);
  }
}
