import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { environment } from '../../../environments/environment';

export interface Investment {
  id?: number;
  stockSymbol: string;
  totalShares: number;
  averageCost: number;
  currentPrice?: number;
  currentValue?: number;
  profitLoss?: number;
  profitLossPercent?: number;
}

export interface PortfolioSummary {
  totalInvested: number;
  currentValue: number;
  totalProfitLoss: number;
  profitLossPercent: number;
  investments: Investment[];
}

@Injectable({
  providedIn: 'root'
})
export class InvestmentService {
  private readonly API_URL = `${environment.apiUrl}/investments`;

  constructor(private http: HttpClient) {}

  getPortfolio(): Observable<PortfolioSummary> {
    return this.http.get<PortfolioSummary>(`${this.API_URL}/portfolio`);
  }

  getInvestments(): Observable<Investment[]> {
    return this.http.get<Investment[]>(this.API_URL);
  }

  createInvestment(investment: Investment): Observable<Investment> {
    return this.http.post<Investment>(this.API_URL, investment);
  }

  deleteInvestment(id: number): Observable<void> {
    return this.http.delete<void>(`${this.API_URL}/${id}`);
  }
}
