// investments.component.ts - åˆå¹¶ Portfolio + Net Worth

import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-investments',
  templateUrl: './investments.component.html',
  styleUrls: ['./investments.component.scss']
})
export class InvestmentsComponent implements OnInit {

  activeTab: 'portfolio' | 'networth' = 'portfolio';

  constructor(private route: ActivatedRoute) {}

  ngOnInit(): void {
    // å¦‚æžœURLä¸­æœ‰fragmentï¼Œåˆ‡æ¢åˆ°å¯¹åº”çš„tab
    this.route.fragment.subscribe(fragment => {
      if (fragment === 'networth') {
        this.activeTab = 'networth';
      }
    });
  }

  switchTab(tab: 'portfolio' | 'networth'): void {
    this.activeTab = tab;
  }
}
<!-- investments.component.html - åˆå¹¶ Portfolio + Net Worth -->
<div class="investments-container">

  <!-- Tab Header -->
  <div class="tab-header">
    <button 
      class="tab-button"
      [class.active]="activeTab === 'portfolio'"
      (click)="switchTab('portfolio')">
      <span class="icon">ðŸ“ˆ</span>
      <span>Portfolio</span>
    </button>
    <button 
      class="tab-button"
      [class.active]="activeTab === 'networth'"
      (click)="switchTab('networth')">
      <span class="icon">ðŸ’°</span>
      <span>Net Worth</span>
    </button>
  </div>

  <!-- Tab Content -->
  <div class="tab-content">
    <!-- Portfolio Tab -->
    <div *ngIf="activeTab === 'portfolio'" class="portfolio-view">
      <app-portfolio></app-portfolio>
    </div>

    <!-- Net Worth Tab -->
    <div *ngIf="activeTab === 'networth'" class="networth-view">
      <app-networth></app-networth>
    </div>
  </div>
</div>
