// networth.component.ts - ä¿®å¤ç‰ˆ

import { Component, OnInit } from '@angular/core';
import { NetWorthService, Asset, Loan } from '../../core/services/networth.service';

@Component({
  selector: 'app-networth',
  templateUrl: './networth.component.html',
  styleUrls: ['./networth.component.scss']
})
export class NetWorthComponent implements OnInit {

  netWorth = 0;
  totalAssets = 0;
  totalLiabilities = 0;

  assets: Asset[] = [];
  loans: Loan[] = [];
  investmentValue = 0;

  loading = false;
  showAddAssetModal = false;
  showAddLoanModal = false;
  showLoanCalculator = false;

  assetTypes = [
    { value: 'CASH', label: 'Cash / Savings', icon: 'ðŸ’°' },
    { value: 'REAL_ESTATE', label: 'Real Estate', icon: 'ðŸ ' },
    { value: 'VEHICLE', label: 'Vehicle', icon: 'ðŸš—' },
    { value: 'OTHER', label: 'Other Assets', icon: 'ðŸ’Ž' }
  ];

  loanTypes = [
    { value: 'MORTGAGE', label: 'Mortgage', icon: 'ðŸ ' },
    { value: 'CAR_LOAN', label: 'Car Loan', icon: 'ðŸš—' },
    { value: 'STUDENT_LOAN', label: 'Student Loan', icon: 'ðŸŽ“' },
    { value: 'PERSONAL_LOAN', label: 'Personal Loan', icon: 'ðŸ’³' },
    { value: 'OTHER', label: 'Other Loan', icon: 'ðŸ“„' }
  ];

  constructor(private networthService: NetWorthService) {}

  ngOnInit(): void {
    this.loadData();
  }

  loadData(): void {
    this.loading = true;

    this.investmentValue = 40000;

    this.assets = [
      { id: 1, assetType: 'CASH', assetName: 'Savings Account', currentValue: 15000, notes: 'Emergency fund' },
      { id: 2, assetType: 'REAL_ESTATE', assetName: 'Primary Home', currentValue: 500000, notes: 'Toronto condo' },
      { id: 3, assetType: 'VEHICLE', assetName: '2020 Honda Civic', currentValue: 25000 }
    ];

    this.loans = [
      {
        id: 1, loanType: 'MORTGAGE', loanName: 'Home Mortgage', principalAmount: 400000,
        interestRate: 3.5, termMonths: 300, startDate: '2020-01-01', endDate: '2045-01-01',
        monthlyPayment: 2000, remainingBalance: 350000, paymentsMade: 48, progressPercent: 12.5
      },
      {
        id: 2, loanType: 'CAR_LOAN', loanName: 'Honda Civic Loan', principalAmount: 30000,
        interestRate: 4.5, termMonths: 60, startDate: '2022-06-01', endDate: '2027-06-01',
        monthlyPayment: 560, remainingBalance: 18000, paymentsMade: 30, progressPercent: 40
      }
    ];

    this.calculateTotals();
    this.loading = false;
  }

  calculateTotals(): void {
    const manualAssets = this.assets.reduce((sum, a) => sum + a.currentValue, 0);
    this.totalAssets = manualAssets + this.investmentValue;
    this.totalLiabilities = this.loans.reduce((sum, l) => sum + l.remainingBalance, 0);
    this.netWorth = this.totalAssets - this.totalLiabilities;
  }

  openAddAssetModal(): void { this.showAddAssetModal = true; }
  openAddLoanModal(): void { this.showAddLoanModal = true; }
  openLoanCalculator(): void { this.showLoanCalculator = true; }

  closeModals(): void {
    this.showAddAssetModal = false;
    this.showAddLoanModal = false;
    this.showLoanCalculator = false;
  }

  onAssetCreated(asset: Asset): void {
    this.assets.push({ ...asset, id: this.assets.length + 1 });
    this.calculateTotals();
    this.closeModals();
  }

  onLoanCreated(loan: Loan): void {
    this.loans.push({ ...loan, id: this.loans.length + 1 });
    this.calculateTotals();
    this.closeModals();
  }

  deleteAsset(id: number): void {
    if (confirm('Delete this asset?')) {
      this.assets = this.assets.filter(a => a.id !== id);
      this.calculateTotals();
    }
  }

  deleteLoan(id: number): void {
    if (confirm('Delete this loan?')) {
      this.loans = this.loans.filter(l => l.id !== id);
      this.calculateTotals();
    }
  }

  getAssetTypeLabel(type: string): string {
    return this.assetTypes.find(t => t.value === type)?.label || type;
  }

  getAssetTypeIcon(type: string): string {
    return this.assetTypes.find(t => t.value === type)?.icon || 'ðŸ’Ž';
  }

  getLoanTypeLabel(type: string): string {
    return this.loanTypes.find(t => t.value === type)?.label || type;
  }

  getLoanTypeIcon(type: string): string {
    return this.loanTypes.find(t => t.value === type)?.icon || 'ðŸ“„';
  }
}
