<div class="networth-container">

  <!-- Net Worth Summary -->
  <div class="networth-summary">
    <div class="summary-card main">
      <div class="icon">ðŸ’Ž</div>
      <div class="info">
        <div class="label">Net Worth</div>
        <div class="amount" [class.positive]="netWorth >= 0" [class.negative]="netWorth < 0">
          {{ netWorth >= 0 ? '+' : '' }}${{ netWorth.toLocaleString('en-US', {minimumFractionDigits: 2}) }}
        </div>
      </div>
    </div>

    <div class="summary-card assets">
      <div class="icon">ðŸ“ˆ</div>
      <div class="info">
        <div class="label">Total Assets</div>
        <div class="amount">${{ totalAssets.toLocaleString('en-US', {minimumFractionDigits: 2}) }}</div>
      </div>
    </div>

    <div class="summary-card liabilities">
      <div class="icon">ðŸ“‰</div>
      <div class="info">
        <div class="label">Total Liabilities</div>
        <div class="amount">${{ totalLiabilities.toLocaleString('en-US', {minimumFractionDigits: 2}) }}</div>
      </div>
    </div>
  </div>

  <!-- Assets Section -->
  <div class="section">
    <div class="section-header">
      <h2>ðŸ’° Assets</h2>
      <button class="btn-add" (click)="openAddAssetModal()">+ Add Asset</button>
    </div>

    <div class="assets-grid">

      <!-- Investment Asset (Auto-calculated) -->
      <div class="asset-card investment">
        <div class="asset-header">
          <span class="asset-icon">ðŸ“Š</span>
          <div class="asset-info">
            <div class="asset-name">Investment Portfolio</div>
            <div class="asset-type">Auto-calculated</div>
          </div>
        </div>
        <div class="asset-value">${{ investmentValue.toLocaleString('en-US', {minimumFractionDigits: 2}) }}</div>
        <div class="asset-note">Synced from Investments</div>
      </div>

      <!-- Manual Assets -->
      <div class="asset-card" *ngFor="let asset of assets">
        <div class="asset-header">
          <span class="asset-icon">{{ getAssetTypeIcon(asset.assetType) }}</span>
          <div class="asset-info">
            <div class="asset-name">{{ asset.assetName }}</div>
            <div class="asset-type">{{ getAssetTypeLabel(asset.assetType) }}</div>
          </div>
          <button class="btn-delete-small" (click)="deleteAsset(asset.id!)">âœ•</button>
        </div>
        <div class="asset-value">${{ asset.currentValue.toLocaleString('en-US', {minimumFractionDigits: 2}) }}</div>
        <div class="asset-note" *ngIf="asset.notes">{{ asset.notes }}</div>
      </div>

    </div>
  </div>

  <!-- Liabilities Section -->
  <div class="section">
    <div class="section-header">
      <h2>ðŸ“‰ Liabilities</h2>
      <div class="header-actions">
        <button class="btn-calculator" (click)="openLoanCalculator()">ðŸ§® Calculator</button>
        <button class="btn-add" (click)="openAddLoanModal()">+ Add Loan</button>
      </div>
    </div>

    <div class="loans-list">

      <div class="loan-card" *ngFor="let loan of loans">
        <div class="loan-header">
          <div class="loan-title">
            <span class="loan-icon">{{ getLoanTypeIcon(loan.loanType) }}</span>
            <div>
              <h3>{{ loan.loanName }}</h3>
              <div class="loan-type">{{ getLoanTypeLabel(loan.loanType) }}</div>
            </div>
          </div>
          <button class="btn-delete" (click)="deleteLoan(loan.id!)">ðŸ—‘ï¸</button>
        </div>

        <div class="loan-details">
          <div class="loan-stats">
            <div class="stat">
              <div class="stat-label">Remaining Balance</div>
              <div class="stat-value">${{ loan.remainingBalance.toLocaleString('en-US', {minimumFractionDigits: 2}) }}</div>
            </div>
            <div class="stat">
              <div class="stat-label">Monthly Payment</div>
              <div class="stat-value">${{ loan.monthlyPayment.toLocaleString('en-US', {minimumFractionDigits: 2}) }}</div>
            </div>
            <div class="stat">
              <div class="stat-label">Interest Rate</div>
              <div class="stat-value">{{ loan.interestRate }}%</div>
            </div>
            <div class="stat">
              <div class="stat-label">Payments Made</div>
              <div class="stat-value">{{ loan.paymentsMade }} / {{ loan.termMonths }}</div>
            </div>
          </div>

          <div class="loan-progress">
            <div class="progress-info">
              <span>Progress</span>
              <span class="progress-percent">{{ loan.progressPercent?.toFixed(1) }}%</span>
            </div>
            <div class="progress-bar">
              <div class="progress-fill" [style.width.%]="loan.progressPercent"></div>
            </div>
          </div>
        </div>
      </div>

      <div class="empty-state" *ngIf="loans.length === 0">
        <p>No loans recorded</p>
        <button (click)="openAddLoanModal()">+ Add Your First Loan</button>
      </div>

    </div>
  </div>

</div>

<!-- Modal Components -->
<app-add-asset-modal 
  *ngIf="showAddAssetModal"
  (close)="closeModals()"
  (assetCreated)="onAssetCreated($event)">
</app-add-asset-modal>

<app-add-loan-modal 
  *ngIf="showAddLoanModal"
  (close)="closeModals()"
  (loanCreated)="onLoanCreated($event)">
</app-add-loan-modal>

<app-loan-calculator-modal 
  *ngIf="showLoanCalculator"
  (close)="closeModals()">
</app-loan-calculator-modal>
