import { Component, OnInit } from '@angular/core';
import { AuthService } from '../../core/auth/auth.service';

interface DashboardStats {
  totalIncome: number;
  totalExpense: number;
  netSavings: number;
  portfolioValue: number;
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent implements OnInit {

  stats: DashboardStats = {
    totalIncome: 0,
    totalExpense: 0,
    netSavings: 0,
    portfolioValue: 0
  };

  recentTransactions: any[] = [];
  loading = false;

  constructor(
    private authService: AuthService
  ) { }

  ngOnInit(): void {
    this.loadDashboardData();
  }

  loadDashboardData(): void {
    this.loading = true;

    // TODO: Load real data from API
    // For now, show placeholder data

    this.stats = {
      totalIncome: 5000,
      totalExpense: 0,
      netSavings: 5000,
      portfolioValue: 0
    };

    this.recentTransactions = [
      {
        id: 1,
        type: 'INCOME',
        category: 'Salary',
        amount: 5000,
        date: new Date(),
        description: 'December Salary'
      }
    ];

    this.loading = false;
  }
}
<div class="dashboard-container">
  <h1>Dashboard</h1>

  <div class="stats-grid" *ngIf="!loading">
    <div class="stat-card income">
      <h3>Total Income</h3>
      <p class="amount">${{ stats.totalIncome | number:'1.2-2' }}</p>
    </div>

    <div class="stat-card expense">
      <h3>Total Expense</h3>
      <p class="amount">${{ stats.totalExpense | number:'1.2-2' }}</p>
    </div>

    <div class="stat-card savings">
      <h3>Net Savings</h3>
      <p class="amount">${{ stats.netSavings | number:'1.2-2' }}</p>
    </div>

    <div class="stat-card portfolio">
      <h3>Portfolio Value</h3>
      <p class="amount">${{ stats.portfolioValue | number:'1.2-2' }}</p>
    </div>
  </div>

  <div class="recent-transactions" *ngIf="!loading">
    <h2>Recent Transactions</h2>
    <table *ngIf="recentTransactions.length > 0">
      <thead>
        <tr>
          <th>Date</th>
          <th>Category</th>
          <th>Description</th>
          <th>Amount</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let transaction of recentTransactions" 
            [class.income]="transaction.type === 'INCOME'"
            [class.expense]="transaction.type === 'EXPENSE'">
          <td>{{ transaction.date | date:'short' }}</td>
          <td>{{ transaction.category }}</td>
          <td>{{ transaction.description }}</td>
          <td>{{ transaction.amount | number:'1.2-2' }}</td>
        </tr>
      </tbody>
    </table>
    <p *ngIf="recentTransactions.length === 0">No transactions yet.</p>
  </div>

  <div class="loading" *ngIf="loading">
    <p>Loading...</p>
  </div>
</div>
