// add-asset-modal.component.ts - ‰øÆÂ§çÁâà

import { Component, EventEmitter, Output } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { NetWorthService, Asset } from '../../../../core/services/networth.service';

@Component({
  selector: 'app-add-asset-modal',
  templateUrl: './add-asset-modal.component.html',
  styleUrls: ['./add-asset-modal.component.scss']
})
export class AddAssetModalComponent {

  @Output() close = new EventEmitter<void>();
  @Output() assetCreated = new EventEmitter<Asset>();

  assetForm: FormGroup;
  submitted = false;
  saving = false;

  assetTypes = [
    { value: 'CASH', label: 'Cash / Savings', icon: 'üí∞', description: 'Bank accounts, cash on hand' },
    { value: 'REAL_ESTATE', label: 'Real Estate', icon: 'üè†', description: 'Properties, land' },
    { value: 'VEHICLE', label: 'Vehicle', icon: 'üöó', description: 'Cars, motorcycles, boats' },
    { value: 'OTHER', label: 'Other Assets', icon: 'üíé', description: 'Jewelry, collectibles, etc.' }
  ];

  constructor(
    private fb: FormBuilder,
    private networthService: NetWorthService
  ) {
    this.assetForm = this.fb.group({
      assetType: ['', Validators.required],
      assetName: ['', Validators.required],
      currentValue: ['', [Validators.required, Validators.min(0)]],
      notes: ['']
    });
  }

  selectAssetType(type: string): void {
    this.assetForm.patchValue({ assetType: type });
  }

  onSubmit(): void {
    this.submitted = true;

    if (this.assetForm.valid && !this.saving) {
      this.saving = true;
      const assetData: Asset = this.assetForm.value;

      this.networthService.createAsset(assetData).subscribe({
        next: (response: Asset) => {
          console.log('Asset created:', response);
          this.assetCreated.emit(response);
          this.closeModal();
        },
        error: (error: any) => {
          console.error('Error creating asset:', error);
          this.saving = false;
          alert('Failed to create asset: ' + (error.message || 'Unknown error'));
        }
      });
    }
  }

  closeModal(): void {
    this.close.emit();
  }

  isFieldInvalid(fieldName: string): boolean {
    const field = this.assetForm.get(fieldName);
    return !!(field && field.invalid && (field.dirty || field.touched || this.submitted));
  }
}
<!-- add-asset-modal.component.html -->

<div class="modal-overlay" (click)="closeModal()">
  <div class="modal-content" (click)="$event.stopPropagation()">

    <div class="modal-header">
      <h2>Add Asset</h2>
      <button class="close-btn" (click)="closeModal()">‚úï</button>
    </div>

    <form [formGroup]="assetForm" (ngSubmit)="onSubmit()">

      <!-- Asset Type Selection -->
      <div class="form-group">
        <label>Asset Type *</label>
        <div class="type-grid">
          <div 
            *ngFor="let type of assetTypes"
            class="type-card"
            [class.selected]="assetForm.get('assetType')?.value === type.value"
            (click)="selectAssetType(type.value)">
            <div class="type-icon">{{ type.icon }}</div>
            <div class="type-label">{{ type.label }}</div>
            <div class="type-description">{{ type.description }}</div>
          </div>
        </div>
        <div class="error-message" *ngIf="isFieldInvalid('assetType')">
          Please select an asset type
        </div>
      </div>

      <!-- Asset Name -->
      <div class="form-group">
        <label>Asset Name *</label>
        <input 
          type="text" 
          formControlName="assetName"
          class="form-control"
          placeholder="e.g., TD Savings Account, Primary Home"
          [class.invalid]="isFieldInvalid('assetName')">
        <div class="error-message" *ngIf="isFieldInvalid('assetName')">
          Please enter an asset name
        </div>
      </div>

      <!-- Current Value -->
      <div class="form-group">
        <label>Current Value ($) *</label>
        <input 
          type="number" 
          formControlName="currentValue"
          class="form-control"
          placeholder="0.00"
          step="0.01"
          [class.invalid]="isFieldInvalid('currentValue')">
        <div class="error-message" *ngIf="isFieldInvalid('currentValue')">
          Please enter a valid amount
        </div>
      </div>

      <!-- Notes -->
      <div class="form-group">
        <label>Notes <span class="optional">(Optional)</span></label>
        <textarea 
          formControlName="notes"
          class="form-control"
          rows="3"
          placeholder="Add any additional notes..."></textarea>
      </div>

      <!-- Action Buttons -->
      <div class="button-group">
        <button type="button" class="btn btn-cancel" (click)="closeModal()">
          Cancel
        </button>
        <button 
          type="submit" 
          class="btn btn-submit"
          [disabled]="saving">
          {{ saving ? 'Saving...' : 'Add Asset' }}
        </button>
      </div>

    </form>

  </div>
</div>
