// src/app/features/profile/profile.component.ts

import { Component, OnInit, OnDestroy } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService, User } from '../../core/auth/auth.service';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.scss']
})
export class ProfileComponent implements OnInit, OnDestroy {
  currentUser: User | null = null;
  isLoading: boolean = true;
  private userSubscription?: Subscription;

  constructor(
    private authService: AuthService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadUserProfile();
  }

  ngOnDestroy(): void {
    this.userSubscription?.unsubscribe();
  }

  loadUserProfile(): void {
    this.isLoading = true;

    // È¶ñÂÖàÂ∞ùËØï‰ªélocalStorageËé∑Âèñ
    const storedUser = localStorage.getItem('currentUser');
    if (storedUser) {
      try {
        this.currentUser = JSON.parse(storedUser);
        console.log('Profile: Loaded user from localStorage:', this.currentUser);
      } catch (error) {
        console.error('Profile: Error parsing stored user:', error);
      }
    }

    // ËÆ¢ÈòÖÂΩìÂâçÁî®Êà∑ÁöÑObservable
    this.userSubscription = this.authService.currentUser.subscribe({
      next: (user) => {
        console.log('Profile: User from subscription:', user);
        this.currentUser = user;
        this.isLoading = false;
      },
      error: (error) => {
        console.error('Profile: Error loading user:', error);
        this.isLoading = false;
      }
    });

    // Â¶ÇÊûúÊ≤°ÊúâÁî®Êà∑Êï∞ÊçÆÔºå‰ªéÊúçÂä°Âô®Âà∑Êñ∞
    if (!this.currentUser) {
      this.refreshUserFromServer();
    } else {
      this.isLoading = false;
    }
  }

  refreshUserFromServer(): void {
    this.authService.getCurrentUser().subscribe({
      next: (user) => {
        console.log('Profile: Refreshed user from server:', user);
        this.currentUser = user;
        this.isLoading = false;
      },
      error: (error) => {
        console.error('Profile: Error refreshing user from server:', error);
        this.isLoading = false;

        // Â¶ÇÊûúËé∑ÂèñÂ§±Ë¥•ÔºåÂèØËÉΩÊòØtokenËøáÊúüÔºåÈúÄË¶ÅÈáçÊñ∞ÁôªÂΩï
        if (error.status === 401) {
          this.logout();
        }
      }
    });
  }

  logout(): void {
    if (confirm('Are you sure you want to logout?')) {
      this.authService.logout();
    }
  }

  navigateToChangePassword(): void {
    this.router.navigate(['/profile/change-password']);
  }

  navigateToDarkMode(): void {
    // ÂÆûÁé∞ÊöóÈªëÊ®°ÂºèÂàáÊç¢
    console.log('Dark mode toggle - to be implemented');
  }

  navigateToCurrency(): void {
    // ÂÆûÁé∞Ë¥ßÂ∏ÅËÆæÁΩÆ
    console.log('Currency settings - to be implemented');
  }

  navigateToNetWorth(): void {
    this.router.navigate(['/investments']);
  }

  navigateToInvestmentPortfolio(): void {
    this.router.navigate(['/investments/portfolio']);
  }

  navigateToHelp(): void {
    console.log('Help & FAQ - to be implemented');
  }

  navigateToSupport(): void {
    console.log('Contact Support - to be implemented');
  }

  getFullName(): string {
    if (!this.currentUser) {
      return 'Loading...';
    }
    return `${this.currentUser.firstName} ${this.currentUser.lastName}`.trim();
  }

  getInitials(): string {
    if (!this.currentUser) {
      return '?';
    }
    const firstInitial = this.currentUser.firstName?.charAt(0) || '';
    const lastInitial = this.currentUser.lastName?.charAt(0) || '';
    return (firstInitial + lastInitial).toUpperCase();
  }

  getMemberSince(): string {
    if (!this.currentUser?.createdAt) {
      return '';
    }
    const date = new Date(this.currentUser.createdAt);
    return date.toLocaleDateString('en-US', { month: 'long', year: 'numeric' });
  }
}
