<div class="register-container">
  <div class="register-card">
    <div class="register-header">
      <h1>Create Account</h1>
      <p class="subtitle">Start managing your finances today</p>
    </div>

    <form [formGroup]="registerForm" (ngSubmit)="onSubmit()">
      <!-- Error Message -->
      <div class="alert alert-error" *ngIf="errorMessage">
        <svg width="20" height="20" viewBox="0 0 20 20" fill="currentColor">
          <path fill-rule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clip-rule="evenodd"/>
        </svg>
        <span>{{ errorMessage }}</span>
      </div>

      <!-- First Name -->
      <div class="form-group">
        <label for="firstName">
          First Name <span class="required">*</span>
        </label>
        <input 
          type="text" 
          id="firstName"
          formControlName="firstName"
          placeholder="John"
          class="form-control"
          [class.error]="registerForm.get('firstName')?.invalid && registerForm.get('firstName')?.touched"
          autocomplete="given-name">
        <div class="error-message" *ngIf="getErrorMessage('firstName')">
          {{ getErrorMessage('firstName') }}
        </div>
      </div>

      <!-- Last Name -->
      <div class="form-group">
        <label for="lastName">
          Last Name <span class="required">*</span>
        </label>
        <input 
          type="text" 
          id="lastName"
          formControlName="lastName"
          placeholder="Doe"
          class="form-control"
          [class.error]="registerForm.get('lastName')?.invalid && registerForm.get('lastName')?.touched"
          autocomplete="family-name">
        <div class="error-message" *ngIf="getErrorMessage('lastName')">
          {{ getErrorMessage('lastName') }}
        </div>
      </div>

      <!-- Email -->
      <div class="form-group">
        <label for="email">
          Email <span class="required">*</span>
        </label>
        <input 
          type="email" 
          id="email"
          formControlName="email"
          placeholder="john.doe@example.com"
          class="form-control"
          [class.error]="registerForm.get('email')?.invalid && registerForm.get('email')?.touched"
          autocomplete="email">
        <div class="error-message" *ngIf="getErrorMessage('email')">
          {{ getErrorMessage('email') }}
        </div>
      </div>

      <!-- Password -->
      <div class="form-group">
        <label for="password">
          Password <span class="required">*</span>
        </label>
        <div class="password-input-wrapper">
          <input 
            [type]="showPassword ? 'text' : 'password'"
            id="password"
            formControlName="password"
            placeholder="••••••••"
            class="form-control"
            [class.error]="registerForm.get('password')?.invalid && registerForm.get('password')?.touched"
            autocomplete="new-password">
          <button 
            type="button" 
            class="password-toggle"
            (click)="togglePasswordVisibility()"
            tabindex="-1">
            <svg *ngIf="!showPassword" width="20" height="20" viewBox="0 0 20 20" fill="currentColor">
              <path d="M10 12a2 2 0 100-4 2 2 0 000 4z"/>
              <path fill-rule="evenodd" d="M.458 10C1.732 5.943 5.522 3 10 3s8.268 2.943 9.542 7c-1.274 4.057-5.064 7-9.542 7S1.732 14.057.458 10zM14 10a4 4 0 11-8 0 4 4 0 018 0z" clip-rule="evenodd"/>
            </svg>
            <svg *ngIf="showPassword" width="20" height="20" viewBox="0 0 20 20" fill="currentColor">
              <path fill-rule="evenodd" d="M3.707 2.293a1 1 0 00-1.414 1.414l14 14a1 1 0 001.414-1.414l-1.473-1.473A10.014 10.014 0 0019.542 10C18.268 5.943 14.478 3 10 3a9.958 9.958 0 00-4.512 1.074l-1.78-1.781zm4.261 4.26l1.514 1.515a2.003 2.003 0 012.45 2.45l1.514 1.514a4 4 0 00-5.478-5.478z" clip-rule="evenodd"/>
              <path d="M12.454 16.697L9.75 13.992a4 4 0 01-3.742-3.741L2.335 6.578A9.98 9.98 0 00.458 10c1.274 4.057 5.065 7 9.542 7 .847 0 1.669-.105 2.454-.303z"/>
            </svg>
          </button>
        </div>
        <div class="error-message" *ngIf="getErrorMessage('password')">
          {{ getErrorMessage('password') }}
        </div>
        <div class="password-hint" *ngIf="registerForm.get('password')?.untouched">
          Password must be at least 8 characters
        </div>
      </div>

      <!-- Confirm Password -->
      <div class="form-group">
        <label for="confirmPassword">
          Confirm Password <span class="required">*</span>
        </label>
        <div class="password-input-wrapper">
          <input 
            [type]="showConfirmPassword ? 'text' : 'password'"
            id="confirmPassword"
            formControlName="confirmPassword"
            placeholder="••••••••"
            class="form-control"
            [class.error]="(registerForm.get('confirmPassword')?.invalid && registerForm.get('confirmPassword')?.touched) || (registerForm.errors?.['passwordMismatch'] && registerForm.get('confirmPassword')?.touched)"
            autocomplete="new-password">
          <button 
            type="button" 
            class="password-toggle"
            (click)="toggleConfirmPasswordVisibility()"
            tabindex="-1">
            <svg *ngIf="!showConfirmPassword" width="20" height="20" viewBox="0 0 20 20" fill="currentColor">
              <path d="M10 12a2 2 0 100-4 2 2 0 000 4z"/>
              <path fill-rule="evenodd" d="M.458 10C1.732 5.943 5.522 3 10 3s8.268 2.943 9.542 7c-1.274 4.057-5.064 7-9.542 7S1.732 14.057.458 10zM14 10a4 4 0 11-8 0 4 4 0 018 0z" clip-rule="evenodd"/>
            </svg>
            <svg *ngIf="showConfirmPassword" width="20" height="20" viewBox="0 0 20 20" fill="currentColor">
              <path fill-rule="evenodd" d="M3.707 2.293a1 1 0 00-1.414 1.414l14 14a1 1 0 001.414-1.414l-1.473-1.473A10.014 10.014 0 0019.542 10C18.268 5.943 14.478 3 10 3a9.958 9.958 0 00-4.512 1.074l-1.78-1.781zm4.261 4.26l1.514 1.515a2.003 2.003 0 012.45 2.45l1.514 1.514a4 4 0 00-5.478-5.478z" clip-rule="evenodd"/>
              <path d="M12.454 16.697L9.75 13.992a4 4 0 01-3.742-3.741L2.335 6.578A9.98 9.98 0 00.458 10c1.274 4.057 5.065 7 9.542 7 .847 0 1.669-.105 2.454-.303z"/>
            </svg>
          </button>
        </div>
        <div class="error-message" *ngIf="getErrorMessage('confirmPassword')">
          {{ getErrorMessage('confirmPassword') }}
        </div>
        <div class="error-message" *ngIf="registerForm.errors?.['passwordMismatch'] && registerForm.get('confirmPassword')?.touched">
          Passwords do not match
        </div>
      </div>

      <!-- Submit Button -->
      <button 
        type="submit" 
        class="btn-register"
        [disabled]="isLoading || registerForm.invalid">
        <span *ngIf="!isLoading">Create Account</span>
        <span *ngIf="isLoading" class="loading-spinner">
          <svg class="spinner" width="20" height="20" viewBox="0 0 20 20">
            <circle cx="10" cy="10" r="8" stroke="currentColor" stroke-width="3" fill="none" stroke-dasharray="50" stroke-dashoffset="0">
              <animateTransform attributeName="transform" type="rotate" from="0 10 10" to="360 10 10" dur="1s" repeatCount="indefinite"/>
            </circle>
          </svg>
          Creating Account...
        </span>
      </button>

      <!-- Login Link -->
      <div class="login-link">
        Already have an account? <a routerLink="/auth/login">Login</a>
      </div>
    </form>
  </div>
</div>
