// src/app/core/auth/auth.service.ts

import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { BehaviorSubject, Observable, tap } from 'rxjs';
import { Router } from '@angular/router';

export interface User {
  id: number;
  email: string;
  firstName: string;
  lastName: string;
  currencyCode?: string;
  createdAt?: string;
  isActive?: boolean;
}

export interface AuthResponse {
  token: string;
  user: User;
}

export interface RegisterRequest {
  firstName: string;
  lastName: string;
  email: string;
  password: string;
}

export interface LoginRequest {
  email: string;
  password: string;
}

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private readonly API_URL = 'http://localhost:8080/api/v1';
  private currentUserSubject: BehaviorSubject<User | null>;
  public currentUser: Observable<User | null>;

  constructor(
    private http: HttpClient,
    private router: Router
  ) {
    const storedUser = localStorage.getItem('currentUser');
    this.currentUserSubject = new BehaviorSubject<User | null>(
      storedUser ? JSON.parse(storedUser) : null
    );
    this.currentUser = this.currentUserSubject.asObservable();

    console.log('AuthService initialized with user:', this.currentUserSubject.value);
  }

  public get currentUserValue(): User | null {
    return this.currentUserSubject.value;
  }

  register(data: RegisterRequest): Observable<AuthResponse> {
    console.log('AuthService: Registering user:', { ...data, password: '***' });

    return this.http.post<AuthResponse>(`${this.API_URL}/auth/register`, data)
      .pipe(
        tap(response => {
          console.log('AuthService: Registration successful:', response);
        })
      );
  }

  login(credentials: LoginRequest): Observable<AuthResponse> {
    console.log('AuthService: Logging in:', credentials.email);

    return this.http.post<AuthResponse>(`${this.API_URL}/auth/login`, credentials)
      .pipe(
        tap(response => {
          console.log('AuthService: Login response received:', response);

          // 保存token
          if (response.token) {
            localStorage.setItem('token', response.token);
            console.log('AuthService: Token saved to localStorage');
          }

          // 保存用户信息
          if (response.user) {
            const userStr = JSON.stringify(response.user);
            localStorage.setItem('currentUser', userStr);
            this.currentUserSubject.next(response.user);
            console.log('AuthService: User saved to localStorage:', response.user);
          }
        })
      );
  }

  logout(): void {
    console.log('AuthService: Logging out user');
    localStorage.removeItem('token');
    localStorage.removeItem('currentUser');
    this.currentUserSubject.next(null);
    this.router.navigate(['/auth/login']);
  }

  getToken(): string | null {
    return localStorage.getItem('token');
  }

  isAuthenticated(): boolean {
    const token = this.getToken();
    if (!token) {
      return false;
    }
    return !this.isTokenExpired(token);
  }

  private isTokenExpired(token: string): boolean {
    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      const expiry = payload.exp * 1000;
      const isExpired = Date.now() > expiry;
      console.log('Token expiry check:', { expiry: new Date(expiry), isExpired });
      return isExpired;
    } catch (error) {
      console.error('Error checking token expiry:', error);
      return true;
    }
  }

  getCurrentUser(): Observable<User> {
    console.log('AuthService: Fetching current user from server');
    return this.http.get<User>(`${this.API_URL}/auth/me`)
      .pipe(
        tap(user => {
          console.log('AuthService: Current user from server:', user);
          localStorage.setItem('currentUser', JSON.stringify(user));
          this.currentUserSubject.next(user);
        })
      );
  }

  updatePassword(oldPassword: string, newPassword: string): Observable<any> {
    return this.http.put(`${this.API_URL}/auth/password`, {
      oldPassword,
      newPassword
    });
  }
}
