// src/app/features/analytics/analytics.component.ts

import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface FinancialSummary {
  totalIncome: number;
  totalExpense: number;
  netAmount: number;
  avgMonthlyIncome: number;
  avgMonthlyExpense: number;
  savingsRate: number;
  monthlySavings: number;
}

@Component({
  selector: 'app-analytics',
  templateUrl: './analytics.component.html',
  styleUrls: ['./analytics.component.scss']
})
export class AnalyticsComponent implements OnInit {
  summary: FinancialSummary | null = null;
  isLoading: boolean = true;
  showEmptyState: boolean = false;
  errorMessage: string = '';

  private readonly API_URL = 'http://localhost:8080/api/v1';

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.loadAnalyticsData();
  }

  loadAnalyticsData(): void {
    this.isLoading = true;
    this.errorMessage = '';
    this.showEmptyState = false;

    // Ëé∑ÂèñËøáÂéª6‰∏™ÊúàÁöÑÊï∞ÊçÆ
    const endDate = new Date();
    const startDate = new Date();
    startDate.setMonth(startDate.getMonth() - 6);

    const params = {
      startDate: startDate.toISOString().split('T')[0],
      endDate: endDate.toISOString().split('T')[0]
    };

    this.http.get<FinancialSummary>(`${this.API_URL}/analytics/income-expense`, { params })
      .subscribe({
        next: (data) => {
          console.log('Analytics data received:', data);
          this.summary = data;

          // Ê£ÄÊü•ÊòØÂê¶ÊúâÊï∞ÊçÆ
          if (data.totalIncome === 0 && data.totalExpense === 0) {
            this.showEmptyState = true;
          }

          this.isLoading = false;
        },
        error: (error) => {
          console.error('Error loading analytics:', error);
          this.isLoading = false;

          // ÊòæÁ§∫Á©∫Áä∂ÊÄÅËÄå‰∏çÊòØÈîôËØØ
          this.showEmptyState = true;
          this.summary = {
            totalIncome: 0,
            totalExpense: 0,
            netAmount: 0,
            avgMonthlyIncome: 0,
            avgMonthlyExpense: 0,
            savingsRate: 0,
            monthlySavings: 0
          };
        }
      });
  }

  formatCurrency(amount: number): string {
    return new Intl.NumberFormat('en-CA', {
      style: 'currency',
      currency: 'CAD',
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }).format(amount || 0);
  }

  formatPercent(value: number): string {
    const sign = value >= 0 ? '+' : '';
    return `${sign}${(value || 0).toFixed(1)}%`;
  }

  getNetAmountClass(): string {
    if (!this.summary) return '';
    return this.summary.netAmount >= 0 ? 'positive' : 'negative';
  }
}
