import { Component, OnInit } from '@angular/core';
import { AuthService } from '../../core/auth/auth.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent implements OnInit {
  userName = '';

  constructor(private authService: AuthService) {}

  ngOnInit(): void {
    const user = this.authService.currentUserValue;
    this.userName = user ? `${user.firstName} ${user.lastName}` : 'User';
  }
}
<div class="dashboard">
  <h1>Welcome, {{userName}}!</h1>
  <div class="quick-actions">
    <button routerLink="/transactions">Transactions</button>
    <button routerLink="/analytics">Analytics</button>
    <button routerLink="/investments">Investments</button>
    <button routerLink="/profile">Profile</button>
  </div>
</div>
